mapper.update:
UPDATE  sp_product_review
SET     review_content = #{reviewContent},
        rating = #{rating}
WHERE   product_no = #{productNo}
AND     consumer_no = #{consumerNo}
AND     review_no = #{reviewNo}

mapper.select:
SELECT  COUNT(*)
FROM 	sp_product_review spr
WHERE 	spr.product_no = #{productNo}
