mapper.update:
UPDATE  sp_product 
SET     total_quantity = total_quantity - #{quantity}
WHERE   product_no = #{productNo}

mapper.delete:
DELETE 
FROM    sp_product_wishlist
WHERE   consumer_no = #{consumerNo}
AND     product_no = #{productNo}

mapper.select:
SELECT  COUNT(*) 
FROM    sp_product_wishlist spw
WHERE   product_no = #{productNo};
